namespace app\core;

use \app\core\middlewares\Middleware;
use app\core\exceptions\NotFoundException;

class Controller {

    /**
     * @var string $currentAction
    */

    public string $action = '';

    /**
     * @var array Variable data generated by extending controllers.
    */

    protected $data = [];

    /*
     * Default layout
    */

    public string $layout = 'main';

    /*
     * Support for additional controller logic, partials
    */

    protected array $children = [];

    /**
     * Set data in current controller
     * @return void
    */

    public function setData(array $data): void {
        $this->data = $data;
    }
    
    /**
     * Array of middleware classes
     * @var app\core\middlewares\Middleware[]
    */

    protected array $middlewares = [];

    /*
     * Set child data
     * Format is Controller:method, can do Controller without :method, will default to Controller:index
    */

    protected function setChildData(array $children) {
        foreach ( $children as $child ) {
            $childAndMethod = preg_match('/:/', $child) ? explode(':', $child) : [$child, 'index'];
            $childController = '\\app\controllers\\'.$childAndMethod[0];
            if (!class_exists($childController)) throw new NotFoundException();
            if (!method_exists($childAndMethod[0], $childAndMethod[1])) throw new NotFoundException();
            $currentChildController = new $childController->{$childAndMethod[1]}();
        }
    }

    /**
     * Get names of children controllers
     * @return array
    */

    public function getChildren() : array {
        return $this->children;
    }

    public function render(string $view, array $params = []) {
        echo Application::$app->view->renderView($view, $params);
    }

    public function setLayout(string $layout) {
        $this->layout = $layout;
    }

    public function registerMiddleware(Middleware $middleware) {
        $this->middlewares[] = $middleware;
    }   

    public function getMiddlewares(): array {
        return $this->middlewares;
    }

}